# Secure Offline Passphrase Generator (PowerShell + WPF, no external modules)
# Notes:
# - Uses cryptographically secure RNG
# - Avoids modulo bias via rejection sampling
# - Wordlist is embedded (replace/expand with your preferred list)

Add-Type -AssemblyName PresentationFramework, PresentationCore, WindowsBase


# -------------------------
# Crypto RNG helpers
# -------------------------
$Rng = [System.Security.Cryptography.RandomNumberGenerator]::Create()

function Get-SecureUInt32 {
  $bytes = New-Object byte[] 4
  $Rng.GetBytes($bytes)
  return [BitConverter]::ToUInt32($bytes, 0)
}

function Get-SecureIntRange {
  param(
    [Parameter(Mandatory)] [int] $MinInclusive,
    [Parameter(Mandatory)] [int] $MaxExclusive
  )
  if ($MaxExclusive -le $MinInclusive) { throw "Invalid range." }

  $range = [uint32]($MaxExclusive - $MinInclusive)

  # Rejection sampling to avoid modulo bias
  $limit = [uint32]::MaxValue - (([uint32]::MaxValue % $range) + 1)

  do { $r = Get-SecureUInt32 } while ($r -gt $limit)

  return $MinInclusive + [int]($r % $range)
}

function Get-RandomWord {
  $idx = Get-SecureIntRange -MinInclusive 0 -MaxExclusive $WordList.Count
  return $WordList[$idx]
}

function Apply-Capitalization {
  param(
    [Parameter(Mandatory)][string]$Word,
    [Parameter(Mandatory)]
    [ValidateSet("None","FirstLetter","FirstWord")]
    [string]$Mode,

    # Default to -1 so we can detect "not passed"
    [int]$Index = -1
  )

  if ([string]::IsNullOrWhiteSpace($Word)) { return $Word }

  switch ($Mode) {
    "None" { return $Word }

    "FirstLetter" {
      return ($Word.Substring(0,1).ToUpperInvariant() + $Word.Substring(1))
    }

    "FirstWord" {
      # Only capitalize the first word (Index 0).
      # If Index wasn't passed, treat it as first word (safer UX).
      if ($Index -eq 0 -or $Index -eq -1) {
        return ($Word.Substring(0,1).ToUpperInvariant() + $Word.Substring(1))
      }
      return $Word
    }
  }
}

function New-Passphrase {
  param(
    [int]$WordCount,
    [string]$Separator,
    [string]$CapsMode,
    [bool]$IncludeNumbers
  )

$parts = for ($i=0; $i -lt $WordCount; $i++) {
  $w = Get-RandomWord
  $w = Apply-Capitalization -Word $w -Mode $CapsMode -Index $i

  if ($IncludeNumbers) {
    $d = Get-SecureIntRange -MinInclusive 0 -MaxExclusive 10
    $w = "$w$d"
  }

  $w
}

  return ($parts -join $Separator)
}

function Get-EntropyEstimateBits {
  param(
    [int]$WordCount,
    [int]$WordListSize,
    [bool]$IncludeNumbers
  )

  # Entropy approx:
  # words: WordCount * log2(WordListSize)
  # numbers: if 1 digit per word => WordCount * log2(10)
  $bitsPerWord = [Math]::Log($WordListSize, 2)
  $bits = $WordCount * $bitsPerWord
  if ($IncludeNumbers) {
    $bits += $WordCount * [Math]::Log(10, 2)
  }

  return [Math]::Round($bits, 1)
}

function Show-TextPrompt {
  param(
    [Parameter(Mandatory)][string]$Title,
    [Parameter(Mandatory)][string]$Message,
    [string]$Default = "",
    [System.Windows.Window]$Owner = $null
  )

  $promptXaml = @"
<Window xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
        xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
        Title="$Title"
        Height="175"
        Width="540"
        WindowStartupLocation="CenterOwner"
        ResizeMode="NoResize"
        Background="#1F1F23">
  <Grid Margin="16">
    <Grid.RowDefinitions>
      <RowDefinition Height="Auto"/>
      <RowDefinition Height="Auto"/>
      <RowDefinition Height="Auto"/>
    </Grid.RowDefinitions>

    <TextBlock Grid.Row="0" Text="$Message" Foreground="#EAEAEA" TextWrapping="Wrap" Margin="0,0,0,10"/>
    <TextBox x:Name="Txt" Grid.Row="1" Height="28" Background="#2B2B31" Foreground="#EAEAEA" BorderThickness="0" Padding="8"/>

    <DockPanel Grid.Row="2" Margin="0,12,0,0" LastChildFill="False">
      <Button x:Name="BtnCancel" Content="Cancel" Width="110" Height="30"
              Background="#2B2B31" Foreground="#EAEAEA" BorderThickness="0"
              DockPanel.Dock="Right" />
      <Button x:Name="BtnOk" Content="OK" Width="110" Height="30" Margin="10,0,0,0"
              Background="#10B981" Foreground="White" BorderThickness="0"
              DockPanel.Dock="Right" />
    </DockPanel>
  </Grid>
</Window>
"@

  [xml]$px = $promptXaml
  $reader = New-Object System.Xml.XmlNodeReader $px
  $w = [Windows.Markup.XamlReader]::Load($reader)

  if ($Owner) { $w.Owner = $Owner }

  $txt = $w.FindName("Txt")
  $txt.Text = $Default

  $ok  = $w.FindName("BtnOk")
  $can = $w.FindName("BtnCancel")

  # Use Window.Tag to carry the result back out of ShowDialog()
  $w.Tag = $null

  $ok.Add_Click({
    $w.Tag = $txt.Text
    $w.DialogResult = $true
    $w.Close()
  })

  $can.Add_Click({
    $w.Tag = $null
    $w.DialogResult = $false
    $w.Close()
  })

  $null = $w.ShowDialog()
  return $w.Tag
}


# -------------------------
# WPF UI
# -------------------------

# Resolve path relative to the script location
$xamlPath = Join-Path $PSScriptRoot 'UI.xaml'

if (-not (Test-Path $xamlPath)) {
    throw "XAML file not found: $xamlPath"
}

# Read and load XAML
[xml]$xaml = Get-Content -LiteralPath $xamlPath -Raw
$reader = New-Object System.Xml.XmlNodeReader $xaml
$Window = [Windows.Markup.XamlReader]::Load($reader)

$TxtPassphrase = $Window.FindName("TxtPassphrase")
$BtnCopy       = $Window.FindName("BtnCopy")
$BtnGenerate   = $Window.FindName("BtnGenerate")
$SldWords      = $Window.FindName("SldWords")
$LblEntropy    = $Window.FindName("LblEntropy")
$LblCharCount  = $Window.FindName("LblCharCount")
$LblStatus = $Window.FindName("LblStatus")
$ChkNums       = $Window.FindName("ChkNums")
$CmbSep        = $Window.FindName("CmbSep")
$CmbCaps       = $Window.FindName("CmbCaps")
$BtnSetADPassword = $Window.FindName("BtnSetADPassword")



function Get-SelectedSeparator {
  $sel = $CmbSep.SelectedItem.Content.ToString()
  switch -Regex ($sel) {
    "\(-\)" { "-" }
    "\(_\)" { "_" }
    "\(\.\)" { "." }
    "\(!\)" { "!" }
    "\(@\)" { "@" }
    "\(#\)" { "#" }
    "\(%\)" { "%" }
    "\(&\)" { "&" }
    default { "" }
  }
}


function Expand-GzipBase64Utf8 {
  param([Parameter(Mandatory)][string]$Base64Gzip)

  $gzipBytes = [Convert]::FromBase64String($Base64Gzip)
  $msIn = New-Object System.IO.MemoryStream(,$gzipBytes)
  try {
    $gz = New-Object System.IO.Compression.GZipStream($msIn, [IO.Compression.CompressionMode]::Decompress)
    try {
      $sr = New-Object System.IO.StreamReader($gz, [Text.Encoding]::UTF8)
      try {
        return $sr.ReadToEnd()
      } finally { $sr.Dispose() }
    } finally { $gz.Dispose() }
  } finally { $msIn.Dispose() }
}


$WordListGzipB64 = @'

'@

$wordText = Expand-GzipBase64Utf8 -Base64Gzip $WordListGzipB64

# Expect one word per line
$WordList = $wordText -split "`r?`n" | Where-Object { $_ -and $_.Trim() -ne "" } | ForEach-Object { $_.Trim().ToLowerInvariant() }

function Show-UserPicker {
  param(
    [Parameter(Mandatory)][string]$Query,
    [Parameter(Mandatory)][object[]]$Users
  )

  $pickerPath = Join-Path $PSScriptRoot 'UserPicker.xaml'
  if (-not (Test-Path $pickerPath)) { throw "Missing picker XAML: $pickerPath" }

  [xml]$x = Get-Content -LiteralPath $pickerPath -Raw
  $r = New-Object System.Xml.XmlNodeReader $x
  $w = [Windows.Markup.XamlReader]::Load($r)

  $w.Owner = $Window

  $lblQuery = $w.FindName("LblQuery")
  $lst      = $w.FindName("LstUsers")
  $btnSel   = $w.FindName("BtnSelect")
  $btnCan   = $w.FindName("BtnCancel")
  $lblStat  = $w.FindName("LblPickStatus")

  $lblQuery.Text = "Query: $Query"
  $lst.ItemsSource = $Users

  $selected = $null

  $btnSel.Add_Click({
    $sel = $lst.SelectedItem
    if ($null -eq $sel) {
      $lblStat.Text = "Select a user from the list."
      return
    }
    $script:selected = $sel
    $w.DialogResult = $true
    $w.Close()
  })

  $btnCan.Add_Click({
    $script:selected = $null
    $w.DialogResult = $false
    $w.Close()
  })

  $null = $w.ShowDialog()
  return $selected
}

function Search-ADUsers {
  param(
    [Parameter(Mandatory)][string]$Query
  )

  # Escape single quotes for the -Filter string
  $q = $Query.Replace("'", "''")

  # Build a single-line filter (avoids newline parsing issues)
  $filter = "(SamAccountName -like '*$q*') -or (Name -like '*$q*') -or (DisplayName -like '*$q*')"

  # Execute search
  $users = Get-ADUser -Filter $filter -Properties DisplayName, UserPrincipalName, DistinguishedName

  # Return projection for the picker
  foreach ($u in $users) {
    $displayName = if ([string]::IsNullOrWhiteSpace($u.DisplayName)) { "<no displayName>" } else { $u.DisplayName }
    $upn = if ([string]::IsNullOrWhiteSpace($u.UserPrincipalName)) { "" } else { $u.UserPrincipalName }

    [pscustomobject]@{
      SamAccountName    = $u.SamAccountName
      DisplayName       = $u.DisplayName
      UserPrincipalName = $u.UserPrincipalName
      DistinguishedName = $u.DistinguishedName
      DisplayLine       = ("{0}  ({1})  {2}" -f $displayName, $u.SamAccountName, $upn)
    }
  }
}

function Reset-ADUserPassword {
  param(
    [Parameter(Mandatory)][string]$SamAccountName,
    [Parameter(Mandatory)][string]$NewPassword
  )

  $secure = ConvertTo-SecureString -String $NewPassword -AsPlainText -Force
  Set-ADAccountPassword -Identity $SamAccountName -Reset -NewPassword $secure -ErrorAction wait
}


function Get-CapsMode {
  $sel = ($CmbCaps.SelectedItem.Content.ToString())
  switch ($sel) {
    "None"         { "None" }
    "First word" { "FirstWord" }
    "Each word" { "FirstLetter" }
    default        { "None" }
  }
}

function Refresh-UI {
  $wordCount = [int]$SldWords.Value
  
  $sep     = Get-SelectedSeparator
  $caps    = Get-CapsMode
  $nums    = [bool]$ChkNums.IsChecked

  $TxtPassphrase.Text = New-Passphrase -WordCount $wordCount -Separator $sep -CapsMode $caps -IncludeNumbers $nums

  $entropy = Get-EntropyEstimateBits -WordCount $wordCount -WordListSize $WordList.Count -IncludeNumbers $nums
  $LblEntropy.Text = "$entropy bits"
  $LblCharCount.Text = "$($TxtPassphrase.Text.Length) chars"
  $LblStatus.Text = ""

}

# Events
$BtnGenerate.Add_Click({ Refresh-UI })

$BtnCopy.Add_Click({
  if ($TxtPassphrase.Text) {
    [System.Windows.Clipboard]::SetText($TxtPassphrase.Text)
    $LblStatus.Text = "Copied to clipboard"
  }
})

$BtnSetADPassword.Add_Click({

  $pass = $TxtPassphrase.Text
  if ([string]::IsNullOrWhiteSpace($pass)) {
    [System.Windows.MessageBox]::Show("Generate a passphrase first.", "No passphrase", "OK", "Warning") | Out-Null
    return
  }

    $query = Show-TextPrompt -Title "Find AD user" -Message "Enter sAMAccountName (or part of name) to search:" -Owner $Window
    if ([string]::IsNullOrWhiteSpace($query)) { [System.Windows.MessageBox]::Show("AD search failed.`n`n No user input detected", "Error", "OK", "Error") | Out-Null
    return
     }

  try {
    $matches = Search-ADUsers -Query $query
  } catch {
    [System.Windows.MessageBox]::Show("AD search failed.`n`n$($_.Exception.Message)", "Error", "OK", "Error") | Out-Null
    return
  }

  if (-not $matches -or $matches.Count -eq 0) {
    [System.Windows.MessageBox]::Show("No users found for: $query", "No results", "OK", "Information") | Out-Null
    return
  }

  $selected = Show-UserPicker -Query $query -Users $matches
  if ($null -eq $selected) { return }

  $confirmMsg = @"
You are about to reset the password for:

Display Name: $($selected.DisplayName)
sAMAccountName: $($selected.SamAccountName)
UPN: $($selected.UserPrincipalName)

Proceed?
"@

  $res = [System.Windows.MessageBox]::Show($confirmMsg, "Confirm password reset", "YesNo", "Warning")
  if ($res -ne "Yes") { return }

  try {
    Reset-ADUserPassword -SamAccountName $selected.SamAccountName -NewPassword $pass
    [System.Windows.MessageBox]::Show("Password updated for $($selected.SamAccountName).", "Success", "OK", "Information") | Out-Null
  } catch {
    [System.Windows.MessageBox]::Show("Failed to set password.`n`n$($_.Exception.Message)", "Error", "OK", "Error") | Out-Null
  }
})


$SldWords.Add_ValueChanged({ Refresh-UI })
$CmbSep.Add_SelectionChanged({ Refresh-UI })
$CmbCaps.Add_SelectionChanged({ Refresh-UI })
$ChkNums.Add_Checked({ Refresh-UI })
$ChkNums.Add_Unchecked({ Refresh-UI })

# Initial
Refresh-UI
$null = $Window.ShowDialog()

# Cleanup RNG
$Rng.Dispose()
